mapper.update:
UPDATE t_materials
   SET m_materialName = #{mMaterialName}, m_donator = #{mDonator}, m_materialType = #{mMaterialType}, m_factdate=#{mFactdate}
 WHERE m_id = #{mId}
   AND u_id = #{uId}

UPDATE t_materials
   SET m_check = 1,m_checkdate = CURDATE()
 WHERE m_id = #{mId}

UPDATE t_materials
   SET m_check = 3,m_date = CURDATE()
 WHERE m_id = #{mId}

UPDATE t_materials
   SET m_check = 2,m_checkdate = CURDATE()
 WHERE m_id = #{mId}
